import React, { Component } from 'react';

import AbakkusNavbar from './AbakkusNavbar.js';
import AbakkusPeopleCard from './AbakkusPeopleCard.js';

import { Container, Columns } from 'react-bulma-components';

const peopleConfig = require('./peopleConfig.json');

class AbakkusPeople extends Component {
    constructor(props) {
        super(props);
        this.state = {

        }
    }

    returnRows(numberOfRows) {
        var peoplePageRows = [];
        var peopleIndex = 0;

        for (let i = 0; i < numberOfRows; i++) {
            var peopleRow = [];
            for (let j = peopleIndex; j < peopleIndex + 4; j ++) {
                peopleRow.push(this.peopleCard(peopleConfig[j]));
            }
            peoplePageRows.push(<Columns style={{ margin: '32px' }}>peopleRow</Columns>)
        }
        console.log(peoplePageRows)
        return peoplePageRows;
    }

    peopleCard(person) {
        if (person === {}) {
            return (<Columns.Column style={{ margin: '16px' }} />);
        }
        return (
            <Columns.Column style={{ margin: '16px' }}>
                <AbakkusPeopleCard name={person.name} position={person.position} description={person.description} img_url={person.img_url} />
            </Columns.Column>
        );
    }

    render() {

        while (peopleConfig.length % 4 != 0) {
            peopleConfig.push({});
        }

        const numberOfRows = Math.ceil(peopleConfig.length / 4);

        var personIndex = 0;
        return (
            <div>
                <AbakkusNavbar />
                {
                    this.returnRows()
                }
            </div>
        )
    }
}

export default AbakkusPeople;
