import React, { Component } from 'react';

import { Button } from 'react-bulma-components';

import UseAnimations from 'react-useanimations';

import twitter from 'react-useanimations/lib/twitter';
import linkedin from 'react-useanimations/lib/linkedin';

class AbakkusPeoplePanel extends Component {
    render() {
        return (
            <div>
                <p style={{ fontSize: '200%' }} >{this.props.name}</p>
                <p style={{ fontSize: '140%' }}>{this.props.position}</p><br />
                <p style={{ fontSize: '110%' }}>{this.props.description}</p>
                <div>
                    <Button renderAs='a' href="https://twitter.com/AbakkusInvest" target="_blank"><strong>Follow Us&nbsp;&nbsp;</strong><UseAnimations animation={twitter}/></Button>
                    <Button renderAs='a' href="https://www.linkedin.com/company/abakkus-asset-managers-llp" target="_blank" style={{ marginLeft: "16px" }}><strong>Follow Us&nbsp;&nbsp;</strong><UseAnimations animation={linkedin}/></Button>
                </div>
            </div>
        )
    }
}

export default AbakkusPeoplePanel;
